import React, { useState } from "react";
import "./Formation.css";

const Formation = ({ diplomas = [], languages = [] }) => {
  const [showPopup, setShowPopup] = useState(false);

  const [popupType, setPopupType] = useState(""); // "diploma" ou "language"
  const [inputValue, setInputValue] = useState("");

  const addItem = () => {
    if (
      popupType === "diploma" &&
      inputValue &&
      !diplomas.includes(inputValue)
    ) {
      diplomas.push(inputValue);
    } else if (
      popupType === "language" &&
      inputValue &&
      !languages.includes(inputValue)
    ) {
      languages.push(inputValue);
    }
    setInputValue("");
    setShowPopup(false);
  };

  const openPopup = (type) => {
    setPopupType(type);
    setShowPopup(true);
  };

  return (
    <section className="formation-section">
      <h2>Formation</h2>

      <div className="diplomas">
        <h3>Diplôme(s)</h3>
        {diplomas.length > 0 ? (
          diplomas.map((diploma, index) => (
            <div key={index} className="diploma-item">
              {diploma}
            </div>
          ))
        ) : (
          <p>Aucun diplôme disponible.</p>
        )}
        <button className="add-button" onClick={() => openPopup("diploma")}>
          Ajouter un diplôme
        </button>
      </div>

      <div className="languages">
        <h3>Langue(s)</h3>
        {languages.length > 0 ? (
          languages.map((language, index) => (
            <div key={index} className="language-item">
              {language}
            </div>
          ))
        ) : (
          <p>Aucune langue disponible.</p>
        )}
        <button className="add-button" onClick={() => openPopup("language")}>
          Ajouter une langue
        </button>
      </div>

      {showPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>
              Ajouter {popupType === "diploma" ? "un diplôme" : "une langue"}
            </h3>
            <input
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              placeholder={`Rechercher ${
                popupType === "diploma" ? "un diplôme" : "une langue"
              }`}
            />
            <div className="popup-buttons">
              <button className="confirm-button" onClick={addItem}>
                Confirmer
              </button>
              <button
                className="cancel-button"
                onClick={() => setShowPopup(false)}
              >
                Annuler
              </button>
            </div>
          </div>
        </div>
      )}
    </section>
  );
};

export default Formation;
